import { useEffect, useState } from "react";
import "../styles/Comments.css";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import { addComment, fetchComments } from "../store/commentsSlice";
import type { CommentModel } from "../types";

export const Comments = () => {
  const dispatch = useAppDispatch();
  const { data: comments, loading, error } = useAppSelector((s) => s.comments);

  const [name, setName] = useState("");
  const [text, setText] = useState("");

  useEffect(() => {
    dispatch(fetchComments());
  }, [dispatch]);

  const submit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!name || !text) return;
    await dispatch(addComment({ name, text }));
    setText("");
  };

  return (
    <div className="comments-wrapper">
      <h2>Comments</h2>

      <form onSubmit={submit} className="comment-form">
        <input
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <textarea
          placeholder="Comment"
          value={text}
          onChange={(e) => setText(e.target.value)}
          required
        />
        <button type="submit">Add</button>
      </form>

      {loading && <p>Loading…</p>}
      {error && <p className="error">{error}</p>}

      <div className="comment-list">
        {comments.map((c: CommentModel) => (
          <div key={c.id} className="comment-item">
            <label className="name-section">{c.name}</label>
            <p className="comment-section">{c.text}</p>
          </div>
        ))}
      </div>
    </div>
  );
};
