// frontend/src/components/auth/OAuthButtons.tsx
import React from "react";
import { API_URL } from "../../lib/api";

type Props = { disabled?: boolean };

const OAuthButtons: React.FC<Props> = ({ disabled }) => {
  const go = (p: "google" | "microsoft") => {
    // Tam URL ile backend'e git (proxy gerekmeden)
    window.location.href = `${API_URL}/api/oauth/${p}`;
  };

  return (
    <div className="oauth-buttons" style={{ display: "grid", gap: 10 }}>
      <button
        type="button"
        disabled={disabled}
        onClick={() => go("google")}
        className="btn oauth google"
      >
        <span style={{ marginRight: 8 }}>ðŸ”‘</span> Continue with Google
      </button>
      <button
        type="button"
        disabled={disabled}
        onClick={() => go("microsoft")}
        className="btn oauth microsoft"
      >
        <span style={{ marginRight: 8 }}>ðŸ’¼</span> Continue with Microsoft
      </button>
    </div>
  );
};

export default OAuthButtons;
