import { useState } from "react";
import { API } from "../../store/api";

export default function Forgot() {
  const [email,setEmail]=useState(""); const [ok,setOk]=useState(false);
  const submit = async ()=>{ await API.post("/api/auth/forgot",{email}); setOk(true); };
  return (
    <div style={{maxWidth:420, margin:"24px auto"}}>
      <h2>Forgot password</h2>
      {ok ? <p>Eğer bu e-postayla bir hesap varsa, sıfırlama linki gönderildi.</p> :
      <>
        <input placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
        <button onClick={submit}>Send reset link</button>
      </>}
    </div>
  );
}
