import React from "react";
import { NavLink, useNavigate } from "react-router-dom";
import "../styles/Navbar.css";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import { logout } from "../store/authSlice";

export const Navbar: React.FC = () => {
  const navigate = useNavigate();
  const dispatch = useAppDispatch();

  const cartCount = useAppSelector((s) =>
    s.cart.items.reduce((a, i) => a + (i.quantity ?? 1), 0)
  );
  const user = useAppSelector((s) => s.auth.user);

  const onSignOut = async () => {
    await dispatch(logout());
    navigate("/login");
  };

  return (
    <div className="navbar">
      <div className="main">
        <div className="mainLink">
          <NavLink to="/" className={({ isActive }) => (isActive ? "active" : "")}>
            Home
          </NavLink>
          <NavLink to="/menu" className={({ isActive }) => (isActive ? "active" : "")}>
            Menu
          </NavLink>
          <NavLink to="/about" className={({ isActive }) => (isActive ? "active" : "")}>
            About
          </NavLink>
          <NavLink to="/comments" className={({ isActive }) => (isActive ? "active" : "")}>
            Comments
          </NavLink>
          <NavLink
            to="/shoppingCartlist"
            className={({ isActive }) => (isActive ? "active cart" : "cart")}
          >
            Shopping Cart{cartCount > 0 && <span className="badge"> {cartCount}</span>}
          </NavLink>

          {/* Auth links (sağda gösterilecek) */}
          {user ? (
            <>
              <span style={{ margin: "0 12px", opacity: 0.85 }}>
                Hello, <b>{user.email}</b>
              </span>
              <button
                onClick={onSignOut}
                style={{
                  border: "none",
                  background: "transparent",
                  color: "#f0a500",
                  cursor: "pointer",
                  fontSize: 16,
                }}
              >
                Sign out
              </button>
            </>
          ) : (
            <>
              <NavLink to="/login" className={({ isActive }) => (isActive ? "active" : "")}>
                Sign in
              </NavLink>
              <NavLink to="/register" className={({ isActive }) => (isActive ? "active" : "")}>
                Register
              </NavLink>
            </>
          )}
        </div>
      </div>
    </div>
  );
};
