import { useEffect, useState } from "react";
import "../styles/Comments.css";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import { addComment, fetchComments } from "../store/commentsSlice";

export const Comments = () => {
  const dispatch = useAppDispatch();
  const comments = useAppSelector(s => s.comments.data);

  const [name, setName] = useState("");
  const [text, setText] = useState("");

  useEffect(() => { dispatch(fetchComments()); }, [dispatch]);

  const submit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!name || !text) return;
    await dispatch(addComment({ name, text }));
    setName("");
    setText("");
  };

  return (
    <div className="comments">
      <form className="comment-box" onSubmit={submit}>
        <div className="name comment-form">
          <label>Type Your Name :</label>
          <input
            value={name}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setName(e.target.value)}
          />
        </div>

        <div className="comment-text comment-form">
          <label>Type Your Comment :</label>
          <textarea
            value={text}
            rows={5}
            onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => setText(e.target.value)}
          />
        </div>

        <button type="submit">Send</button>
      </form>

      <div className="comment-list comment-box">
        <h2>Comments</h2>
        <div className="comment">
          {comments.map(c => (
            <div key={c.id} className="comment-item">
              <label className="name-section">{c.name}</label>
              <p className="comment-section">{c.text}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};
