import { useEffect } from "react";
import { MenuItem } from "./MenuItem";
import "../styles/Menu.css";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import { fetchItems, setParams } from "../store/itemsSlice";

export const Menu = () => {
  const dispatch = useAppDispatch();
  const { data, loading, error, total, params } = useAppSelector(s => s.items);

  useEffect(() => { dispatch(fetchItems()); }, [dispatch]);

  const onSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(setParams({ q: e.target.value, page: 1 }));
  };
  const onCategory = (e: React.ChangeEvent<HTMLSelectElement>) => {
    dispatch(setParams({ category: e.target.value || undefined, page: 1 }));
  };
  const onSort = (e: React.ChangeEvent<HTMLSelectElement>) => {
    dispatch(setParams({ sort: e.target.value as any, page: 1 }));
  };
  const apply = () => dispatch(fetchItems());

  const next = () => { dispatch(setParams({ page: (params.page ?? 1) + 1 })); dispatch(fetchItems()); };
  const prev = () => { const p = Math.max(1, (params.page ?? 1) - 1); dispatch(setParams({ page: p })); dispatch(fetchItems()); };
  const totalPages = Math.max(1, Math.ceil(total / (params.limit ?? 12)));

  return (
    <div className="menuContainer">
      <h1 className="menu-title">Menu</h1>

      {/* FILTER BAR */}
      <div style={{display:'grid', gridTemplateColumns:'1fr 160px 180px 120px', gap:12, marginBottom:12}}>
        <input placeholder="Search..." defaultValue={params.q} onChange={onSearch} />
        <select defaultValue={params.category ?? ''} onChange={onCategory}>
          <option value="">All categories</option>
          <option value="Burgers">Burgers</option>
          <option value="Wraps">Wraps</option>
          <option value="Salads">Salads</option>
        </select>
        <select defaultValue={params.sort ?? 'newest'} onChange={onSort}>
          <option value="newest">Newest</option>
          <option value="price_asc">Price â</option>
          <option value="price_desc">Price â</option>
          <option value="rating_desc">Rating â</option>
        </select>
        <button onClick={apply}>Apply</button>
      </div>

      {/* LIST */}
      <div className="menu-items">
        {loading ? <p>Loading...</p> :
         error ? <p style={{color:'crimson'}}>Error: {error}</p> :
         (data.length===0 ? <p>No items available</p> :
            data.map(m => (
              <div className="menu-item" key={m.id}><MenuItem menuItem={m} /></div>
            ))
         )}
      </div>

      {/* PAGINATION */}
      <div style={{display:'flex', gap:12, justifyContent:'center', alignItems:'center', marginTop:16}}>
        <button onClick={prev} disabled={(params.page ?? 1) <= 1}>Prev</button>
        <span>Page {(params.page ?? 1)} / {totalPages}</span>
        <button onClick={next} disabled={(params.page ?? 1) >= totalPages}>Next</button>
      </div>
    </div>
  );
};
