import React, { useState } from "react";
import "../../styles/Auth.css";
import OAuthButtons from "./OAuthButtons";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [busy, setBusy] = useState(false);
  const [err, setErr] = useState("");

  const submit = async (e: React.FormEvent) => {
    e.preventDefault();
    setErr("");
    setBusy(true);
    try {
      const res = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type":"application/json" },
        credentials: "include",
        body: JSON.stringify({ email, password })
      });
      if (!res.ok) {
        const j = await res.json().catch(()=> ({}));
        throw new Error(j?.error || "Login failed");
      }
      window.location.href = "/";
    } catch (e:any) {
      setErr(e.message || "Error");
    } finally { setBusy(false); }
  };

  return (
    <div className="auth-wrap">
      <div className="auth-card">
        <div className="auth-title">Welcome back</div>
        <div className="auth-sub">E-posta ile giriş yapabilir ya da hesabını bağlayabilirsin.</div>

        <form className="form-grid" onSubmit={submit}>
          <input className="input" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} type="email" />
          <input className="input" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} type="password" />
          {err && <div className="err">{err}</div>}
          <button className="btn full" disabled={busy}>{busy? "Please wait..." : "Sign in"}</button>
        </form>

        <div className="hr">or</div>
        <OAuthButtons />
        <p className="note">
          Hesabın yok mu? <a href="/register">Create account</a>
        </p>
      </div>
    </div>
  );
}
