// frontend/src/components/auth/Register.tsx
import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import OAuthButtons from "./OAuthButtons";
import { postJSON } from "../../lib/api";

const Register: React.FC = () => {
  const nav = useNavigate();
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [busy, setBusy] = useState(false);
  const [err, setErr] = useState<string | null>(null);

  const submit = async (e: React.FormEvent) => {
    e.preventDefault();
    setErr(null);
    if (!email || !password) {
      setErr("Email ve şifre zorunlu.");
      return;
    }
    try {
      setBusy(true);
      await postJSON<{ id: number; email: string; name?: string }>(
        "/api/auth/register",
        { name: name || null, email, password }
      );
      // cookie set ediliyor; kullanıcı giriş yapmış oluyor
      nav("/"); // veya nav("/menu");
    } catch (ex: any) {
      setErr(ex?.message || "Register failed");
    } finally {
      setBusy(false);
    }
  };

  return (
    <div className="auth-page" style={{ display: "grid", placeItems: "center" }}>
      <div
        className="card"
        style={{
          width: 520,
          maxWidth: "95vw",
          background: "#fff",
          padding: 24,
          borderRadius: 14,
          boxShadow: "0 10px 30px rgba(0,0,0,.10)",
        }}
      >
        <h2 style={{ textAlign: "center", marginTop: 6, marginBottom: 6 }}>
          Create account
        </h2>
        <p style={{ textAlign: "center", marginTop: 0, color: "#666" }}>
          Siparişlerini takip et, yorum yap, sepetini sakla.
        </p>

        <form onSubmit={submit} style={{ display: "grid", gap: 10 }}>
          <input
            placeholder="Name (optional)"
            value={name}
            onChange={(e) => setName(e.target.value)}
            disabled={busy}
          />
          <input
            placeholder="Email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            disabled={busy}
          />
          <input
            placeholder="Password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            disabled={busy}
          />

          {err && (
            <div style={{ color: "crimson", fontSize: 14, textAlign: "center" }}>
              {err}
            </div>
          )}

          <button type="submit" className="btn primary" disabled={busy}>
            {busy ? "Registering..." : "Register"}
          </button>
        </form>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr auto 1fr",
            alignItems: "center",
            gap: 12,
            marginTop: 12,
            color: "#aaa",
          }}
        >
          <hr />
          <span>or</span>
          <hr />
        </div>

        <OAuthButtons disabled={busy} />

        <div style={{ textAlign: "center", marginTop: 12, fontSize: 14 }}>
          Hesabın var mı? <Link to="/login">Sign in</Link>
        </div>
      </div>
    </div>
  );
};

export default Register;
