import { useState, useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { login, me } from "../../store/authSlice";

export default function Login() {
  const d = useAppDispatch();
  const { user, loading, error } = useAppSelector(s => s.auth);
  const [email, setEmail] = useState(""); const [password, setPassword] = useState("");

  useEffect(()=>{ d(me()); }, [d]);

  if (user) return <p style={{textAlign:'center'}}>Signed in as <b>{user.email}</b></p>;

  return (
    <div style={{maxWidth:420, margin:"24px auto"}}>
      <h2>Sign in</h2>
      <input placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
      <input placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} />
      <button disabled={loading} onClick={()=>d(login({email,password}))}>{loading?"..." : "Sign in"}</button>
      {error && <p style={{color:"crimson"}}>{error}</p>}
      <p style={{opacity:.7, marginTop:8}}>Email doğrulanmamışsa giriş reddedilir.</p>
    </div>
  );
}
