import React from "react";
import { NavLink } from 'react-router-dom';
import '../styles/Navbar.css';
import { useAppSelector } from "../store/hooks";

export const Navbar = () => {
  const count = useAppSelector(s => s.cart.items.reduce((a,i)=>a + (i.quantity ?? 1), 0));

  return (
    <div className="navbar">
      <div className="main">
        <div className="mainLink">
          <NavLink to="/" className={({ isActive }) => isActive ? 'active' : ''}>Home</NavLink>
          <NavLink to="/menu" className={({ isActive }) => isActive ? 'active' : ''}>Menu</NavLink>
          <NavLink to="/about" className={({ isActive }) => isActive ? 'active' : ''}>About</NavLink>
          <NavLink to="/comments" className={({ isActive }) => isActive ? 'active' : ''}>Comments</NavLink>
          <NavLink to="/shoppingCartlist" className={({ isActive }) => isActive ? 'active cart' : 'cart'}>
            Shopping Cart {count > 0 && <span className="badge">{count}</span>}
          </NavLink>
        </div>
      </div>
    </div>
  );
};
