import React, { useState } from "react";
import "../../styles/Auth.css";

export default function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [busy, setBusy] = useState(false);
  const [err, setErr] = useState("");

  const submit = async (e: React.FormEvent) => {
    e.preventDefault();
    setErr("");
    setBusy(true);
    try {
      const res = await fetch("/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ name, email, password }),
      });
      if (!res.ok) {
        const j = await res.json().catch(() => ({}));
        throw new Error(j?.error || "Register failed");
      }
      window.location.href = "/"; // login oldu – cookie ayarlandı
    } catch (e: any) {
      setErr(e.message || "Error");
    } finally {
      setBusy(false);
    }
  };

  return (
    <div className="auth-wrap">
      <div className="auth-card">
        <div className="auth-title">Create account</div>
        <div className="auth-sub">Siparişlerini takip et, yorum yap, sepetini sakla.</div>

        <form className="form-grid" onSubmit={submit}>
          <input className="input" placeholder="Name (optional)" value={name} onChange={e=>setName(e.target.value)} />
          <input className="input" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} type="email" />
          <input className="input" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} type="password" />
          {err && <div className="err">{err}</div>}
          <button className="btn full" disabled={busy}>{busy? "Please wait..." : "Register"}</button>
        </form>

        <div className="hr">or</div>
        <OAuthButtons />
        <p className="note">Hesabın var mı? <a href="/login">Sign in</a></p>
      </div>
    </div>
  );
}

import OAuthButtons from "./OAuthButtons";
