import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import axios from 'axios';
import { API } from '../lib/api';
import type { Item } from '../types';

type State = { data: Item[]; loading: boolean; error?: string | null };
const initial: State = { data: [], loading: false, error: null };

export const fetchItems = createAsyncThunk<Item[]>('items/fetch', async () => {
  const { data } = await axios.get<Item[]>(`${API}/api/items`);
  return data;
});

const slice = createSlice({
  name: 'items',
  initialState: initial,
  reducers: {},
  extraReducers: b => {
    b.addCase(fetchItems.pending, s => { s.loading = true; s.error = null; })
     .addCase(fetchItems.fulfilled, (s, a) => { s.loading = false; s.data = a.payload; })
     .addCase(fetchItems.rejected, (s, a) => { s.loading = false; s.error = a.error.message ?? 'failed'; });
  }
});

export default slice.reducer;
