// Trailing slash temizleyelim ve default'u 8080 yapalım
const base = (process.env.REACT_APP_API_URL ?? 'http://localhost:8080').replace(/\/+$/, '');
export const API = base;
export const apiUrl = (path: string) => `${base}${path.startsWith('/') ? '' : '/'}${path}`;

