import { useEffect, useState } from "react";
import { API } from "../../store/api";

export default function Reset() {
  const [password,setPassword]=useState(""); const [token,setToken]=useState<string|null>(null);
  const [msg,setMsg]=useState<string|undefined>();
  useEffect(()=>{ setToken(new URLSearchParams(window.location.search).get("token")); },[]);
  const submit = async ()=> {
    try { await API.post("/api/auth/reset",{token,password}); setMsg("Şifre güncellendi. Giriş yapabilirsin."); }
    catch(e:any){ setMsg(e?.response?.data?.error || "Hata"); }
  };
  if(!token) return <p style={{textAlign:"center"}}>Token yok.</p>;
  return (
    <div style={{maxWidth:420, margin:"24px auto"}}>
      <h2>Reset password</h2>
      <input placeholder="New password" type="password" value={password} onChange={e=>setPassword(e.target.value)} />
      <button onClick={submit}>Update</button>
      {msg && <p>{msg}</p>}
    </div>
  );
}
