import { useEffect, useState } from "react";
import { API } from "../../store/api";

export default function Verify() {
  const [msg, setMsg] = useState("Verifying...");
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get("token");
    if (!token) { setMsg("Token yok"); return; }
    API.get(`/api/auth/verify?token=${encodeURIComponent(token)}`)
      .then(()=> setMsg("E-posta doğrulandı. Artık giriş yapabilirsin."))
      .catch(()=> setMsg("Token geçersiz veya süresi dolmuş."));
  }, []);
  return <p style={{textAlign:"center", marginTop:24}}>{msg}</p>;
}
